SELECT *
FROM JAVA_REPLY
WHERE 
	boardno = #{boardno}
ORDER BY REPLYNO DESC
LIMIT #{cri.pageStart},#{cri.replyPageNum}

SELECT BOARDNO
FROM JAVA_REPLY
WHERE replyno = #{replyno}

